mapper.update:
UPDATE EAT_RESTAURANT
SET RS_RATING=(SELECT ROUND(AVG(RS_RATING),1) FROM RESTAURANT_REVIEW WHERE RS_SEQ=#{seq})
WHERE SEQ=#{seq}

mapper.select:
SELECT * FROM (SELECT ROWNUM AS RNUM,SEQ,RS_NAME,RS_ADDRESS1,RS_ADDRESS2,RS_PICTURE,RS_CATEGORY,RS_MENU,RS_RATING,RS_READCOUNT,RS_KEYWORD FROM EAT_RESTAURANT WHERE 1=1
AND RS_CATEGORY LIKE '%'||#{s_category}||'%'
) A WHERE RNUM BETWEEN ${start} AND ${end}

SELECT NVL(COUNT(*), 0) 
FROM RESTAURANT_LIKES
WHERE ID=#{id} AND RS_SEQ=#{rs_seq}

mapper.update:
UPDATE EAT_RESTAURANT SET
RS_ADDRESS1=#{rs_address1}, RS_ADDRESS2=#{rs_address2}, RS_CATEGORY=#{rs_category}, RS_MENU=#{rs_menu}, RS_RATING=#{rs_rating}, RS_READCOUNT=#{rs_readcount}, RS_KEYWORD=#{rs_keyword}    
WHERE SEQ=#{seq}

UPDATE RESTAURANT_INFO SET 
RS_TIME=#{rs_time}, RS_PHONE=#{rs_phone}, RS_PRICE=#{rs_price}, RS_PARKING=#{rs_parking}
WHERE RS_SEQ = #{seq}
